import React from 'react';
import { useLocation } from 'react-router-dom';
import './SecondBanner.css'; // Assuming you style the banner here

const SecondBanner = () => {
    const location = useLocation();
    
    // Define banners for each route
    const main_banners = {
        '/treks': {
        bgImage: 'trek-page-bg.jpg',
        heading: 'Explore Treks'
        },
        '/homestays': {
        bgImage: 'homestays-page-bg.jpg',
        heading: 'Find Homestays'
        },
        '/contact': {
        bgImage: 'contact-page-bg.jpg',
        heading: 'Contact Us'
        }
    };

    // Get the banner for the current route
    const banner = main_banners[location.pathname] || {
        bgImage: 'default-bg-image.jpg', 
        heading: 'Welcome' 
    };

    return (
        <div
        className="main_banner"
        style={{
            backgroundImage: `url(${main_banners.bgImage})`,
            height: '300px', // Adjust as per your design
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        }}
        >
        <h1 style={{ color: 'white', fontSize: '3rem' }}>{main_banners.heading}</h1>
        </div>
    );
};

export default SecondBanner;
