// BannerCarousel.js
import React from 'react';
import Slider from 'react-slick';
import './Banner.css';

// Import Slick CSS
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const BannerCarousel = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3, // Show 3 images at a time
        slidesToScroll: 1,
    };

    return (
        <Slider {...settings}>
            <div>
                <img
                    className="carousel-image"
                    src="/assets/images/trek.jpg"
                    alt="First slide"
                />
                <div className="carousel-caption">
                    <h3>First Slide Label</h3>
                    <p>Experience the beauty of Uttarakhand.</p>
                </div>
            </div>
            <div>
                <img
                    className="carousel-image"
                    src="/assets/images/homestays.jpg"
                    alt="Second slide"
                />
                <div className="carousel-caption">
                    <h3>Second Slide Label</h3>
                    <p>Adventure awaits in the land of gods.</p>
                </div>
            </div>
            <div>
                <img
                    className="carousel-image"
                    src="/assets/images/travel.jpg"
                    alt="Third slide"
                />
                <div className="carousel-caption">
                    <h3>Third Slide Label</h3>
                    <p>Explore the unexplored with Travel_Uttarakhand.</p>
                </div>
            </div>
        </Slider>
    );
};

export default BannerCarousel;
